mapper.select:
SELECT br_nm , br_cd FROM br_info WHERE
user_no like '%'
user_no = #{USER_NO}

SELECT user_no , br_cd , br_nm , br_adr , br_adr2 , TO_CHAR(cls_dt) AS cls_dt , CONCAT(CONCAT(br_adr, ', '), br_adr2) AS BR_ADR3 FROM br_info WHERE
user_no LIKE '%' AND br_cd = #{BR_CD}
user_no = #{USER_NO}
user_no LIKE '%' AND br_cd LIKE '%'
user_no = #{USER_NO} AND br_cd = #{BR_CD}

SELECT mfbiz_id , ( SELECT mfbiz_nm FROM mfbiz_info WHERE mfbiz_info.mfbiz_id = devc_info.mfbiz_id ) AS mfbiz_nm , user_no , model_id , devc_no , ( SELECT dtl_cdnm FROM code_info WHERE grp_cdid = '2' AND dtl_cdid = ( SELECT devc_stat FROM devc_stat WHERE devc_info.devc_no = devc_stat.devc_no ) ) AS dtl_cdnm , ( SELECT devc_stat FROM devc_stat WHERE devc_info.devc_no = devc_stat.devc_no ) AS devc_stat , indc_date , dscd_date , devc_ip , os_info , cpu_info , sw_ver , dscd_date FROM devc_info WHERE
mfbiz_id LIKE '%' AND user_no = #{USER_NO}
mfbiz_id = #{MFBIZ_ID}
mfbiz_id LIKE '%'
mfbiz_id = #{MFBIZ_ID} AND user_no = #{USER_NO}

SELECT ( SELECT mfbiz_nm FROM mfbiz_info WHERE mfbiz_info.mfbiz_id = devc_info.mfbiz_id ) AS mfbiz_nm , devc_no , os_info , cpu_info , sw_ver FROM devc_info WHERE
mfbiz_id = #{MFBIZ_ID}
user_no = #{USER_NO}
mfbiz_id LIKE '%'
user_no = #{USER_NO} AND mfbiz_id = #{MFBIZ_ID}

mapper.insert:
INSERT INTO
	devc_info
	(
		user_no
		, mfbiz_id
		, model_id
		, devc_no
		, indc_date
		, reg_dt
		, devc_ip
		, os_info
		, cpu_info
		, sw_ver
	)
	VALUES
	(
		#{USER_NO}
		, #{MFBIZ_ID}
		, #{MODEL_ID}
		, #{DEVC_NO}
		, #{INDC_DATE}
		, #{REG_DT}
		, #{DEVC_IP}
		, #{OS_INFO}
		, #{CPU_INFO}
		, #{SW_VER}
	)

INSERT INTO
	devc_info_hist
	(
		user_no
		, mfbiz_id
		, model_id
		, devc_no
		, indc_date
		, reg_dt
		, devc_ip
		, os_info
		, cpu_info
		, sw_ver
		, seq
	)
	VALUES
	(
		#{USER_NO}
		, #{MFBIZ_ID}
		, #{MODEL_ID}
		, #{DEVC_NO}
		, #{INDC_DATE}
		, #{REG_DT}
		, #{DEVC_IP}
		, #{OS_INFO}
		, #{CPU_INFO}
		, #{SW_VER}
		, '1'
	)

INSERT INTO
	DEVC_INFO_HIST
	(
		user_no
		, mfbiz_id
		, model_id
		, devc_no
		, seq
		, devc_ip
		, os_info
		, cpu_info
		, sw_ver
		, indc_date
		, reg_dt


				, dscd_date


	)
	VALUES
	(
		#{USER_NO}
		, #{MFBIZ_ID}
		, #{MODEL_ID}
		, #{DEVC_NO}
		, (
			(
				SELECT
					MAX(seq)
				FROM
					devc_info_hist
				WHERE
					devc_no = #{DEVC_NO}
			) + 1
		)
		, #{DEVC_IP}
		, #{OS_INFO}
		, #{CPU_INFO}
		, #{SW_VER}
		, #{INDC_DATE}
		, #{REG_DT}


				, #{DSCD_DATE}


	)

INSERT INTO
	devc_stat
	(
		devc_no
		, user_no
		, devc_stat
		, reg_dt
	)
	VALUES (
		#{DEVC_NO}
		, #{USER_NO}
		, '1'
		, #{REG_DT}
	)

INSERT INTO
	devc_stat_hist
	(
		devc_no
		, user_no
		, devc_stat
		, reg_dt
		, seq
	)
	VALUES(
		#{DEVC_NO}
		, #{USER_NO}
		, '1'
		, #{REG_DT}
		, '1'
	)

INSERT INTO
	devc_stat_hist
	(
		devc_no
		, user_no
		, devc_stat
		, reg_dt
		, seq
	)
	VALUES (
		#{DEVC_NO}
		, #{USER_NO}
		, #{DEVC_STAT}
		, #{REG_DT}
		, (
			(
				SELECT
					MAX(seq)
				FROM
					devc_stat_hist
				WHERE
					devc_no = #{DEVC_NO}
			) +1
		)
	)

INSERT INTO
	user_info
	(
		cntrl_user_id
		, user_no
		, user_pw
		, user_nm
		, user_hp
		, user_email
		, cntrl_author
		, reg_dt
	)
	VALUES
	(
		#{CNTRL_USER_ID}
		, NVL(
			(
				SELECT
					MAX(user_no)
				FROM
					user_info
			) +1, 1
		)
		, #{USER_PW}
		, '-'
		, #{USER_HP}
		, #{USER_EMAIL}
		, '01'
		, #{REG_DT}
	)

INSERT INTO
	model_info
	(
		mfbiz_id
		, model_id
		, model_nm
		, use_yn
		, reg_dt
	)
	VALUES
	(
		#{MFBIZ_ID}
		, #{MODEL_ID}
		, #{MODEL_NM}
		, #{USE_YN}
		, #{REG_DT}
	)

INSERT INTO
	code_info
	(
		grp_cdid
		, dtl_cdid
		, grp_cdnm
		, dtl_cdnm
		, use_yn
		, reg_dt
	)
	VALUES
	(
		#{GRP_CDID}
		, #{DTL_CDID}
		, #{GRP_CDNM}
		, #{DTL_CDNM}
		, #{USE_YN}
		, #{REG_DT}
	)

mapper.update:
UPDATE
	goods_cd_info
SET
	goods_cdnm = #{GOODS_CDNM}
	, mdfcn_dt = #{MDFCN_DT}
WHERE
	goods_type = #{GOODS_TYPE}
AND goods_cd = #{GOODS_CD}

UPDATE
	br_info
SET
	user_no = #{USER_NO}
	, br_nm = #{BR_NM}
	, br_adr = #{BR_ADR}
	, br_adr2 = #{BR_ADR2}
	, mdfcn_dt = #{MDFCN_DT}
WHERE
	user_no = #{USER_NO}
AND br_cd = #{BR_CD}

UPDATE
	br_info
SET
	mdfcn_dt = #{MDFCN_DT}
	, cls_dt = #{CLS_DT}
WHERE
	user_no = #{USER_NO}
AND br_cd = #{BR_CD}
AND cls_dt IS NULL

UPDATE
	devc_info
SET
	devc_ip = #{DEVC_IP}
	, os_info = #{OS_INFO}
	, cpu_info = #{CPU_INFO}
	, sw_ver = #{SW_VER}
	, mdfcn_dt = #{MDFCN_DT}
WHERE
	devc_no = #{DEVC_NO}

UPDATE
	devc_info
SET
	mdfcn_dt = #{MDFCN_DT}
	, dscd_date= #{DSCD_DATE}
WHERE
	devc_no = #{DEVC_NO}

UPDATE
	devc_stat
SET
	devc_stat = #{DEVC_STAT}
	, mdfcn_dt = #{MDFCN_DT}
WHERE
	devc_no = #{DEVC_NO}

UPDATE
	user_info
SET
	user_pw = #{USER_PW}
	, user_hp = #{USER_HP}
	, user_email = #{USER_EMAIL}
	, mdfcn_dt = #{MDFCN_DT}
WHERE
	user_no = #{USER_NO}

UPDATE
	user_info
SET
	cntrl_user_id = #{CNTRL_USER_ID}
	, user_pw = #{USER_PW}
	, user_hp = #{USER_HP}
	, user_email = #{USER_EMAIL}
	, mdfcn_dt = #{MDFCN_DT}
WHERE
	cntrl_user_id = #{CNTRL_USER_ID}

UPDATE
	mfbiz_info
SET
	mfbiz_id = #{MFBIZ_ID}
	, mfbiz_nm = #{MFBIZ_NM}
	, mntmng_tel = #{MNTMNG_TEL}
	, use_yn = #{USE_YN}
	, mdfcn_dt = #{MDFCN_DT}
WHERE
	mfbiz_id = #{MFBIZ_ID}

UPDATE
	model_info
SET
	mfbiz_id = #{MFBIZ_ID}
	, model_nm = #{MODEL_NM}
	, model_id = #{MODEL_ID}
	, use_yn = #{USE_YN}
	, mdfcn_dt = #{MDFCN_DT}
WHERE
	mfbiz_id = #{MFBIZ_ID}
AND	model_id = #{MODEL_ID}

UPDATE
	code_info
SET
	grp_cdid = #{GRP_CDID}
	, dtl_cdid = #{DTL_CDID}
	, grp_cdnm = #{GRP_CDNM}
	, dtl_cdnm = #{DTL_CDNM}
	, use_yn = #{USE_YN}
	, mdfcn_dt = #{MDFCN_DT}
WHERE
	grp_cdid = #{GRP_CDID}
AND	dtl_cdid = #{DTL_CDID}

mapper.delete:
DELETE FROM
	goods_cd_info
WHERE
	goods_type = #{GOODS_TYPE}
AND	goods_cd = #{GOODS_CD}

DELETE FROM
	model_info
WHERE
	mfbiz_id = #{MFBIZ_ID}
AND model_id = #{MODEL_ID}

DELETE FROM
	code_info
WHERE
	grp_cdid = #{GRP_CDID}
AND	dtl_cdid = #{DTL_CDID}
