mapper.select:
SELECT br_nm, br_cd FROM br_info WHERE
user_eno like '%'
user_eno=#{USER_ENO}

 	SELECT
 		user_eno, br_cd, br_nm, br_adr, br_adr2, TO_CHAR(cls_dt) AS cls_dt, CONCAT(CONCAT(br_adr, ', '), br_adr2) AS br_adr3
 	FROM
 		br_info
 	WHERE


			user_eno LIKE '%' AND br_cd =#{BR_CD}			


			user_eno = #{USER_ENO}		


			user_eno LIKE '%' AND br_cd LIKE '%'			


			 user_eno=#{USER_ENO} AND br_cd =#{BR_CD}

SELECT mfbiz_id, ( SELECT mfbiz_nm FROM mfbiz_info WHERE mfbiz_info.mfbiz_id=devc_info.mfbiz_id ) AS mfbiz_nm, user_eno, model_id, devc_uno, (SELECT dtl_cdnm FROM code_info WHERE grp_cdid = '2' AND dtl_cdid = ( SELECT devc_stat FROM devc_stat WHERE devc_info.devc_uno = devc_stat.devc_uno
)) AS dtl_cdnm ,( SELECT devc_stat FROM devc_stat WHERE devc_info.devc_uno = devc_stat.devc_uno
) AS devc_stat ,indc_date ,dscd_date ,devc_ip ,os_info ,cpu_info ,sw_ver ,dscd_date
FROM devc_info WHERE
mfbiz_id LIKE '%' AND user_eno = #{USER_ENO}
mfbiz_id = #{MFBIZ_ID}
mfbiz_id LIKE '%'
mfbiz_id = #{MFBIZ_ID} AND user_eno = #{USER_ENO}

SELECT ( SELECT mfbiz_nm FROM mfbiz_info WHERE mfbiz_info.mfbiz_id=devc_info.mfbiz_id ) AS mfbiz_nm, devc_uno, os_info, cpu_info, sw_ver FROM devc_info WHERE
mfbiz_id = #{MFBIZ_ID}
user_eno = #{USER_ENO}
mfbiz_id LIKE '%'
user_eno = #{USER_ENO} AND mfbiz_id = #{MFBIZ_ID}

mapper.insert:
		MERGE INTO goods_cd_info c
        USING DUAL
        ON(c.goods_cdnm = #{GOODS_CDNM} AND c.goods_type=#{GOODS_TYPE})
        WHEN NOT MATCHED THEN
        INSERT(c.goods_type, c.goods_cd, c.goods_cdnm, c.use_yn, c.reg_dt)
		VALUES
			(
				#{GOODS_TYPE}
				,NVL((SELECT MAX(goods_cd) FROM goods_cd_info WHERE goods_type=#{GOODS_TYPE})+1,001)
				,#{GOODS_CDNM}
				,'Y'
				,#{REG_DT}
			)

INSERT INTO 
	br_info (user_eno, br_cd, br_nm, br_adr, br_adr2, reg_dt)
VALUES (
		#{USER_ENO}
		,NVL((SELECT MAX(br_cd) FROM br_info WHERE user_eno=#{USER_ENO})+1,1)
		,#{BR_NM}
		,#{BR_ADR}
		,#{BR_ADR2}
		,#{REG_DT}
		)

 	INSERT
	INTO
	    devc_info_hist(
	        user_eno
	        ,mfbiz_id
	        ,model_id
	        ,devc_uno
	        ,seq
	        ,devc_ip
	        ,os_info
	        ,cpu_info
	        ,sw_ver
	        ,indc_date
	        ,reg_dt


	        		,dscd_date


	    )
	VALUES
	(	#{USER_ENO}
	    ,#{MFBIZ_ID}
	    ,#{MODEL_ID}
	    ,#{DEVC_UNO}
	    ,((SELECT MAX(seq) FROM devc_info_hist WHERE devc_uno = #{DEVC_UNO}) + 1)
	    ,#{DEVC_IP}
	    ,#{OS_INFO}
	    ,#{CPU_INFO}
	    ,#{SW_VER}
	    ,#{INDC_DATE}
	    ,#{REG_DT}


	        	,#{DSCD_DATE}


	)

 	INSERT INTO 
 		devc_stat_hist(
 			devc_uno
			,user_eno
			,devc_stat
			,reg_dt
			,seq
 		)
	VALUES(
		#{DEVC_UNO}
		,#{USER_ENO}
		,#{DEVC_STAT}
		,#{REG_DT}
		,((SELECT MAX(SEQ) FROM DEVC_STAT_HIST WHERE DEVC_UNO = #{DEVC_UNO}) +1)
	)

mapper.update:

	 	UPDATE
			goods_cd_info
		SET            	
			goods_cdnm=#{GOODS_CDNM}	
			,mdfcn_dt=#{MDFCN_DT}       
        WHERE
            goods_type = #{GOODS_TYPE} AND goods_cd = #{GOODS_CD}

UPDATE 
	br_info
SET
	user_eno = #{USER_ENO}
	,br_nm = #{BR_NM}
	,br_adr = #{BR_ADR}
	,br_adr2 = #{BR_ADR2}
	,mdfcn_dt = #{MDFCN_DT}
WHERE
	user_eno = #{USER_ENO} AND br_cd = #{BR_CD}


 	UPDATE 
	    devc_info
	SET
	    devc_ip = #{DEVC_IP}
	    ,os_info = #{OS_INFO}
	    ,cpu_info = #{CPU_INFO}
	    ,sw_ver = #{SW_VER}
	    ,mdfcn_dt = #{MDFCN_DT}
	WHERE
	    devc_uno = #{DEVC_UNO}

UPDATE 
	devc_info
SET
	mdfcn_dt = #{MDFCN_DT}
	,dscd_date= #{DSCD_DATE}
WHERE
	devc_uno=#{DEVC_UNO}

 	UPDATE
 		devc_stat
 	SET
 		devc_stat = #{DEVC_STAT}
		,mdfcn_dt = #{MDFCN_DT}
	WHERE
		devc_uno = #{DEVC_UNO}

UPDATE 
	user_info
SET
	user_pw = #{USER_PW}
	,user_hp = #{USER_HP}
	,user_email = #{USER_EMAIL}
	,mdfcn_dt = #{MDFCN_DT}
WHERE 
	user_eno = #{USER_ENO}


UPDATE 
	user_info
SET
	cntrl_user_id = #{CNTRL_USER_ID}
	,user_pw = #{USER_PW}
	,user_hp = #{USER_HP}
	,user_email = #{USER_EMAIL}
	,mdfcn_dt = #{MDFCN_DT}
WHERE 
	cntrl_user_id = #{CNTRL_USER_ID}

 	UPDATE 
 		mfbiz_info
 	SET
 		mfbiz_id = #{MFBIZ_ID}
		,mfbiz_nm = #{MFBIZ_NM}
		,mntmng_tel = #{MNTMNG_TEL}
		,use_yn = #{USE_YN}
		,mdfcn_dt = #{MDFCN_DT}
	WHERE
		mfbiz_id = #{MFBIZ_ID}

 	UPDATE 
 		model_info
 	SET
 		mfbiz_id = #{MFBIZ_ID}
		,model_nm = #{MODEL_NM}
		,model_id = #{MODEL_ID}
		,use_yn = #{USE_YN}
		,mdfcn_dt = #{MDFCN_DT}
	WHERE
		mfbiz_id = #{MFBIZ_ID}
	AND
		model_id = #{MODEL_ID}

 	UPDATE 
 		code_info
 	SET
 		grp_cdid = #{GRP_CDID}
		,dtl_cdid = #{DTL_CDID}
		,grp_cdnm = #{GRP_CDNM}
		,dtl_cdnm = #{DTL_CDNM}
		,use_yn = #{USE_YN}
		,mdfcn_dt = #{MDFCN_DT}
	WHERE
		grp_cdid = #{GRP_CDID}
	AND
		dtl_cdid = #{DTL_CDID}

mapper.delete:
 	DELETE FROM code_info 
 	WHERE 
 		grp_cdid = #{GRP_CDID}
	AND
		dtl_cdid = #{DTL_CDID}
