mapper.select:
SELECT BR_NM, BR_CD FROM BR_INFO WHERE
USER_ENO like '%'
USER_ENO=#{USER_ENO}

SELECT USER_ENO, BR_CD, BR_NM, BR_ADR, BR_ADR2, to_char(CLS_DT) AS CLS_DT, CONCAT(CONCAT(BR_ADR, ', '), BR_ADR2) AS BR_ADR3 FROM BR_INFO WHERE
USER_ENO like '%' AND BR_CD =#{BR_CD}
USER_ENO = #{USER_ENO}
USER_ENO like '%' AND BR_CD like '%'
USER_ENO=#{USER_ENO} AND BR_CD =#{BR_CD}

SELECT MFBIZ_ID , ( SELECT MFBIZ_NM FROM MFBIZ_INFO WHERE MFBIZ_INFO.MFBIZ_ID = DEVC_INFO.MFBIZ_ID ) AS MFBIZ_NM , USER_ENO , MODEL_ID , DEVC_UNO , ( SELECT DTL_CDNM FROM CODE_INFO WHERE GRP_CDID = '2' AND DTL_CDID = ( SELECT DEVC_STAT FROM DEVC_STAT WHERE DEVC_INFO.DEVC_UNO = DEVC_STAT.DEVC_UNO ) ) AS DTL_CDNM , ( SELECT DEVC_STAT FROM DEVC_STAT WHERE DEVC_INFO.DEVC_UNO = DEVC_STAT.DEVC_UNO ) AS DEVC_STAT , INDC_DATE , DSCD_DATE , DEVC_IP , OS_INFO , CPU_INFO , SW_VER , DSCD_DATE FROM DEVC_INFO WHERE
MFBIZ_ID LIKE '%' AND USER_ENO = #{USER_ENO}
MFBIZ_ID = #{MFBIZ_ID}
MFBIZ_ID LIKE '%'
MFBIZ_ID = #{MFBIZ_ID} AND USER_ENO = #{USER_ENO}

SELECT ( SELECT MFBIZ_NM FROM MFBIZ_INFO WHERE MFBIZ_INFO.MFBIZ_ID = DEVC_INFO.MFBIZ_ID ) AS MFBIZ_NM , DEVC_UNO , OS_INFO , CPU_INFO , SW_VER FROM DEVC_INFO WHERE
MFBIZ_ID = #{MFBIZ_ID}
USER_ENO = #{USER_ENO}
MFBIZ_ID LIKE '%'
USER_ENO = #{USER_ENO} AND MFBIZ_ID = #{MFBIZ_ID}

mapper.insert:
INSERT INTO
	DEVC_INFO
	(
		USER_ENO
		, MFBIZ_ID
		, MODEL_ID
		, DEVC_UNO
		, INDC_DATE
		, REG_DT
		, DEVC_IP
		, OS_INFO
		, CPU_INFO
		, SW_VER
	)
	VALUES
	(
		#{USER_ENO}
		, #{MFBIZ_ID}
		, #{MODEL_ID}
		, #{DEVC_UNO}
		, #{INDC_DATE}
		, #{REG_DT}
		, #{DEVC_IP}
		, #{OS_INFO}
		, #{CPU_INFO}
		, #{SW_VER}
	)

INSERT INTO
	DEVC_INFO_HIST
	(
		USER_ENO
		, MFBIZ_ID
		, MODEL_ID
		, DEVC_UNO
		, INDC_DATE
		, REG_DT
		, DEVC_IP
		, OS_INFO
		, CPU_INFO
		, SW_VER
		, SEQ
	)
	VALUES
	(
		#{USER_ENO}
		, #{MFBIZ_ID}
		, #{MODEL_ID}
		, #{DEVC_UNO}
		, #{INDC_DATE}
		, #{REG_DT}
		, #{DEVC_IP}
		, #{OS_INFO}
		, #{CPU_INFO}
		, #{SW_VER}
		, '1'
	)

INSERT INTO
	DEVC_INFO_HIST
	(
		USER_ENO
		, MFBIZ_ID
		, MODEL_ID
		, DEVC_UNO
		, SEQ
		, DEVC_IP
		, OS_INFO
		, CPU_INFO
		, SW_VER
		, INDC_DATE
		, REG_DT


				, DSCD_DATE


	)
	VALUES
	(
		#{USER_ENO}
		, #{MFBIZ_ID}
		, #{MODEL_ID}
		, #{DEVC_UNO}
		, (
			(
				SELECT
					MAX(SEQ)
				FROM
					DEVC_INFO_HIST
				WHERE
					DEVC_UNO = #{DEVC_UNO}
			) + 1
		)
		, #{DEVC_IP}
		, #{OS_INFO}
		, #{CPU_INFO}
		, #{SW_VER}
		, #{INDC_DATE}
		, #{REG_DT}


				, #{DSCD_DATE}


	)

INSERT INTO
	DEVC_STAT
	(
		DEVC_UNO
		, USER_ENO
		, DEVC_STAT
		, REG_DT
	)
	VALUES (
		#{DEVC_UNO}
		, #{USER_ENO}
		, '1'
		, #{REG_DT}
	)

INSERT INTO
	DEVC_STAT_HIST
	(
		DEVC_UNO
		, USER_ENO
		, DEVC_STAT
		, REG_DT
		, SEQ
	)
	VALUES(
		#{DEVC_UNO}
		, #{USER_ENO}
		, '1'
		, #{REG_DT}
		, '1'
	)

INSERT INTO
	DEVC_STAT_HIST
	(
		DEVC_UNO
		, USER_ENO
		, DEVC_STAT
		, REG_DT
		, SEQ
	)
	VALUES (
		#{DEVC_UNO}
		, #{USER_ENO}
		, #{DEVC_STAT}
		, #{REG_DT}
		, (
			(
				SELECT
					MAX(SEQ)
				FROM
					DEVC_STAT_HIST
				WHERE
					DEVC_UNO = #{DEVC_UNO}
			) +1
		)
	)

INSERT INTO
	USER_INFO
	(
		CNTRL_USER_ID
		, USER_ENO
		, USER_PW
		, USER_NM
		, USER_HP
		, USER_EMAIL
		, CNTRL_AUTHOR
		, REG_DT
	)
	VALUES
	(
		#{CNTRL_USER_ID}
		, NVL(
			(
				SELECT
					MAX(USER_ENO)
				FROM
					USER_INFO
			) +1, 1
		)
		, #{USER_PW}
		, '-'
		, #{USER_HP}
		, #{USER_EMAIL}
		, '01'
		, #{REG_DT}
	)

INSERT INTO
	MODEL_INFO
	(
		MFBIZ_ID
		, MODEL_ID
		, MODEL_NM
		, USE_YN
		, REG_DT
	)
	VALUES
	(
		#{MFBIZ_ID}
		, #{MODEL_ID}
		, #{MODEL_NM}
		, #{USE_YN}
		, #{REG_DT}
	)

INSERT INTO
	CODE_INFO
	(
		GRP_CDID
		, DTL_CDID
		, GRP_CDNM
		, DTL_CDNM
		, USE_YN
		, REG_DT
	)
	VALUES
	(
		#{GRP_CDID}
		, #{DTL_CDID}
		, #{GRP_CDNM}
		, #{DTL_CDNM}
		, #{USE_YN}
		, #{REG_DT}
	)

mapper.update:
UPDATE
	GOODS_CD_INFO
SET
	GOODS_CDNM = #{GOODS_CDNM}
	, MDFCN_DT = #{MDFCN_DT}
WHERE
	GOODS_TYPE = #{GOODS_TYPE}
AND GOODS_CD = #{GOODS_CD}

UPDATE
	BR_INFO
SET
	USER_ENO = #{USER_ENO}
	, BR_NM = #{BR_NM}
	, BR_ADR = #{BR_ADR}
	, BR_ADR2 = #{BR_ADR2}
	, MDFCN_DT = #{MDFCN_DT}
WHERE
	USER_ENO = #{USER_ENO}
AND BR_CD = #{BR_CD}

UPDATE
	BR_INFO
SET
	MDFCN_DT = #{MDFCN_DT}
	, CLS_DT = #{CLS_DT}
WHERE
	USER_ENO = #{USER_ENO}
AND BR_CD = #{BR_CD}
AND CLS_DT IS NULL

UPDATE
	DEVC_INFO
SET
	DEVC_IP = #{DEVC_IP}
	, OS_INFO = #{OS_INFO}
	, CPU_INFO = #{CPU_INFO}
	, SW_VER = #{SW_VER}
	, MDFCN_DT = #{MDFCN_DT}
WHERE
	DEVC_UNO = #{DEVC_UNO}

UPDATE
	DEVC_INFO
SET
	MDFCN_DT = #{MDFCN_DT}
	, DSCD_DATE= #{DSCD_DATE}
WHERE
	DEVC_UNO = #{DEVC_UNO}

UPDATE
	DEVC_STAT
SET
	DEVC_STAT = #{DEVC_STAT}
	, MDFCN_DT = #{MDFCN_DT}
WHERE
	DEVC_UNO = #{DEVC_UNO}

UPDATE
	USER_INFO
SET
	USER_PW = #{USER_PW}
	, USER_HP = #{USER_HP}
	, USER_EMAIL = #{USER_EMAIL}
	, MDFCN_DT = #{MDFCN_DT}
WHERE
	USER_ENO = #{USER_ENO}

UPDATE
	USER_INFO
SET
	CNTRL_USER_ID = #{CNTRL_USER_ID}
	, USER_PW = #{USER_PW}
	, USER_HP = #{USER_HP}
	, USER_EMAIL = #{USER_EMAIL}
	, MDFCN_DT = #{MDFCN_DT}
WHERE
	CNTRL_USER_ID = #{CNTRL_USER_ID}

UPDATE
	MFBIZ_INFO
SET
	MFBIZ_ID = #{MFBIZ_ID}
	, MFBIZ_NM = #{MFBIZ_NM}
	, MNTMNG_TEL = #{MNTMNG_TEL}
	, USE_YN = #{USE_YN}
	, MDFCN_DT = #{MDFCN_DT}
WHERE
	MFBIZ_ID = #{MFBIZ_ID}

UPDATE
	MODEL_INFO
SET
	MFBIZ_ID = #{MFBIZ_ID}
	, MODEL_NM = #{MODEL_NM}
	, MODEL_ID = #{MODEL_ID}
	, USE_YN = #{USE_YN}
	, MDFCN_DT = #{MDFCN_DT}
WHERE
	MFBIZ_ID = #{MFBIZ_ID}
AND	MODEL_ID = #{MODEL_ID}

UPDATE
	CODE_INFO
SET
	GRP_CDID = #{GRP_CDID}
	, DTL_CDID = #{DTL_CDID}
	, GRP_CDNM = #{GRP_CDNM}
	, DTL_CDNM = #{DTL_CDNM}
	, USE_YN = #{USE_YN}
	, MDFCN_DT = #{MDFCN_DT}
WHERE
	GRP_CDID = #{GRP_CDID}
AND	DTL_CDID = #{DTL_CDID}

mapper.delete:
DELETE FROM
	GOODS_CD_INFO
WHERE
	GOODS_TYPE = #{GOODS_TYPE}
AND	GOODS_CD = #{GOODS_CD}

DELETE FROM
	MODEL_INFO
WHERE
	MFBIZ_ID = #{MFBIZ_ID}
AND MODEL_ID = #{MODEL_ID}

DELETE FROM
	CODE_INFO
WHERE
	GRP_CDID = #{GRP_CDID}
AND	DTL_CDID = #{DTL_CDID}
