mapper.select:
SELECT br_nm , br_cd FROM br_info WHERE
user_eno like '%'
user_eno=#{USER_ENO}

SELECT ocrn_date , br_cd , devc_uno , user_eno , ob_type , ob_strt_tm , ac_cd , NVL2(ac_cd, '접수','비접수') as reg_yn , NVL(ac_dts, '비접수') AS ac_dts , NVL(reqre_tm, '비접수') AS reqre_tm , reg_dt , mdfcn_dt , ob_end_tm
( SELECT br_nm FROM br_info WHERE o.br_cd = br_info.br_cd AND br_info.user_eno = '%' ) AS br_nm , ( SELECT mfbiz_nm FROM mfbiz_info WHERE mfbiz_id = ( SELECT mfbiz_id FROM devc_info WHERE devc_uno=o.devc_uno AND user_eno = '%' ) ) AS mfbiz_nm , ( SELECT mntmng_tel FROM mfbiz_info WHERE mfbiz_id = ( SELECT mfbiz_id FROM devc_info WHERE devc_uno = o.devc_uno AND user_eno = '%' ) ) AS mntmng_tel
( SELECT br_nm FROM br_info WHERE o.br_cd = br_info.br_cd AND br_info.user_eno = #{USER_ENO} ) AS br_nm , ( SELECT mfbiz_nm FROM mfbiz_info WHERE mfbiz_id = ( SELECT mfbiz_id FROM devc_info WHERE devc_uno = o.devc_uno AND user_eno = #{USER_ENO} ) ) AS mfbiz_nm , ( SELECT mntmng_tel FROM mfbiz_info WHERE mfbiz_id = ( SELECT mfbiz_id FROM devc_info WHERE devc_uno = o.devc_uno AND user_eno = #{USER_ENO} ) ) AS mntmng_tel
FROM ob_stat o WHERE
user_eno like '%' and br_cd=#{BR_CD}
user_eno=#{USER_ENO}
user_eno like '%' and br_cd like '%'
user_eno=#{USER_ENO} and br_cd=#{BR_CD}

mapper.insert:
INSERT into
	ob_stat_hist
	(
		ocrn_date
		, br_cd
		, devc_uno
		, user_eno
		, seq
		, ob_type
		, ob_strt_tm
		, reg_dt
	)
	VALUES
	(
		#{OCRN_DATE}
		, #{BR_CD}
		, #{DEVC_UNO}
		, #{USER_ENO}
		, '1'
		, #{OB_TYPE}
		, #{OB_STRT_TM}
		, #{REG_DT}
	)

mapper.select:
SELECT ocrn_date , br_cd , devc_uno , user_eno , ob_type , ob_strt_tm , ac_cd , NVL2(ac_cd, '접수','접수 대기중') as reg_yn , NVL(ac_dts, '접수 대기중') AS ac_dts , NVL(reqre_tm, '접수 대기중') AS reqre_tm , reg_dt , mdfcn_dt , ob_end_tm ( SELECT br_nm FROM br_info WHERE br_info.br_cd = o.br_cd AND br_info.user_eno = o.user_eno ) AS br_nm , ( SELECT mfbiz_nm FROM mfbiz_info WHERE mfbiz_id =( SELECT mfbiz_id FROM devc_info WHERE devc_uno=o.devc_uno AND user_eno=o.user_eno ) ) AS mfbiz_nm , ( SELECT mntmng_tel FROM mfbiz_info WHERE mfbiz_id = ( SELECT mfbiz_id FROM devc_info WHERE devc_uno = o.devc_uno AND user_eno = o.user_eno ) ) AS mntmng_tel FROM ob_stat o WHERE
user_eno like '%' AND br_cd like '%'
user_eno like '%' AND br_cd = #{BR_CD}

mapper.insert:
INSERT into
	ob_stat_hist
	(
		ocrn_date
		, br_cd
		, devc_uno
		, user_eno
		, seq
		, ob_type
		, ob_strt_tm
		, reg_dt
		, ac_cd
		, AC_DTS
	)
VALUES
(
	#{OCRN_DATE}
	, #{BR_CD}
	, #{DEVC_UNO}
	, #{USER_ENO}
	, (
		SELECT MAX(seq)
		FROM ob_stat_hist
		WHERE
			ocrn_date = #{OCRN_DATE}
		AND br_cd = #{BR_CD}
		AND devc_uno = #{DEVC_UNO}
		AND user_eno= #{USER_ENO})+1
	, #{OB_TYPE}
	, #{OB_STRT_TM}
	, #{REG_DT}
	, #{AC_CD}
	, #{AC_DTS}
)
