mapper.select:
SELECT br_nm, br_cd FROM br_info WHERE
user_eno like '%'
user_eno=#{USER_ENO}

	 	SELECT
    		SUM(dlamt)
		FROM 
		    sales_info
		WHERE




			     TO_CHAR(delng_date)=#{DELNG_DATE} AND user_eno=#{USER_ENO}





			        TO_CHAR(delng_date,'YY/MM/DD')=#{DELNG_DATE}





			      br_cd=#{BR_CD} AND TO_CHAR(delng_date,'YY/MM/DD')=#{DELNG_DATE} AND user_eno=#{USER_ENO}

	 	SELECT
    		SUM(dlamt)
		FROM 
		    sales_info
		WHERE




			     TO_CHAR(delng_date) >=#{DELNG_DATE} AND TO_CHAR(delng_date) <= #{DELNG_DATE2} AND user_eno=#{USER_ENO}





			         TO_CHAR(delng_date) >=#{DELNG_DATE} AND TO_CHAR(delng_date) <= #{DELNG_DATE2} AND user_eno=#{USER_ENO}





			      br_cd=#{BR_CD} AND user_eno=#{USER_ENO} AND  TO_CHAR(delng_date) >=#{DELNG_DATE} AND TO_CHAR(delng_date) <= #{DELNG_DATE2} AND user_eno=#{USER_ENO}

SELECT TO_CHAR(delng_date) AS delng_date, user_eno, br_cd, devc_uno, goods_cd, delng_seq, delng_tm, dlamt, dlnt,
( SELECT br_nm FROM br_info WHERE br_info.br_cd = sales_info.br_cd AND br_info.user_eno = sales_info.user_eno ) AS br_nm ,
( SELECT goods_cdnm FROM goods_cd_info WHERE sales_info.goods_cd = goods_cd_info.goods_cd and sales_info.goods_type = goods_cd_info.goods_type ) AS goods_nm
( SELECT br_nm FROM br_info WHERE sales_info.br_cd = br_info.br_cd AND br_info.user_eno = #{USER_ENO} ) AS br_nm ,
( SELECT goods_cdnm FROM goods_cd_info WHERE sales_info.goods_cd = goods_cd_info.goods_cd AND sales_info.goods_type = goods_cd_info.goods_type ) AS goods_nm
FROM sales_info WHERE
TO_CHAR(delng_date,'YY/MM/DD') =#{DELNG_DATE} AND user_eno=#{USER_ENO}
TO_CHAR(delng_date,'YY/MM/DD') =#{DELNG_DATE}
br_cd=#{BR_CD} AND user_eno=#{USER_ENO} AND TO_CHAR(delng_date) =#{DELNG_DATE}

SELECT TO_CHAR(delng_date) AS delng_date, user_eno, br_cd, devc_uno, goods_cd, delng_seq, delng_tm, dlamt, dlnt,
( SELECT br_nm FROM br_info WHERE br_info.br_cd = sales_info.br_cd AND br_info.user_eno = sales_info.user_eno ) AS br_nm ,
( SELECT goods_cdnm FROM goods_cd_info WHERE sales_info.goods_cd = goods_cd_info.goods_cd AND sales_info.goods_type = goods_cd_info.goods_type ) AS goods_nm
( SELECT br_nm FROM br_info WHERE sales_info.br_cd = br_info.br_cd AND br_info.user_eno = #{USER_ENO} ) AS br_nm ,
( SELECT goods_cdnm FROM goods_cd_info WHERE sales_info.goods_cd = goods_cd_info.goods_cd AND sales_info.goods_type = goods_cd_info.goods_type ) AS goods_nm
FROM sales_info WHERE
TO_CHAR(delng_date) >=#{DELNG_DATE} AND TO_CHAR(delng_date) <= #{DELNG_DATE2} AND user_eno=#{USER_ENO}
TO_CHAR(delng_date) >=#{DELNG_DATE} AND TO_CHAR(delng_date) <= #{DELNG_DATE2}
br_cd=#{BR_CD} AND user_eno=#{USER_ENO} AND TO_CHAR(delng_date) >=#{DELNG_DATE} AND TO_CHAR(delng_date) <= #{DELNG_DATE2}

mapper.update:
    	UPDATE 
